import { BOX_SIZE, GRID_LENGTH, SUDOKU_SIZE, GRID_COORDS } from '@sudoku/constants';

export const nextBoard = Array.from({ length: 9 }, () =>
             Array.from({ length: 9 }, () =>
               Array(10).fill(0)
             )
       );

//
export function updateCandidates(row, col){
    let num = nextBoard[row][col][0];
    for(let pos = 0; pos < SUDOKU_SIZE; pos++){
        nextBoard[row][pos][num] = 0;
        nextBoard[pos][col][num] = 0;
        nextBoard[row][col][pos + 1] = 0
    }

    let box_row = Math.floor(row / 3) * 3;
    let box_col = Math.floor(col / 3) * 3;
    for(let r = box_row; r <= box_row + 2; r++){
        for(let c = box_col; c <= box_col + 2; c++){
            nextBoard[r][c][num] = 0;
        }
    }
}